import React from "react";
import { Link } from "react-router-dom";
import "../App.css";

// Funtional component with props
const BookCard = props => {

  // Receiving prop book wich is a object
  const book = props.book;
  return (

    // retriving data from one book
    <section className="card-container">
      <figure>
        <img
          src="https://commapress.co.uk/books/the-book-of-cairo/cairo-provisional-v3/image%2Fspan3"
          alt=""
        />
      </figure>
      <div className="desc">
        <h2>
          <Link to={`/show-book/${book._id}`} id={book._id}>
            {book.title}
          </Link>
        </h2>
        <h3>{book.author}</h3>
        <p>{book.description}</p>
      </div>
    </section>
  );
};

export default BookCard;
