import React, { Component } from "react";
import "../App.css";
import axios from "axios";
import { Link } from "react-router-dom";
import BookCard from "./BookCard";

// READ-obtener-GET
class ShowBookList extends Component {

  // constructor
  constructor(props) {
    super(props);

    // initial state
    this.state = {
      books: []
    };
  }

  // Once component has been mount
  componentDidMount() {
    axios

      // GET is used to request data from a specified resource.
      .get("http://localhost:4000/api/books")
      .then(res => {
        this.setState({

          // reciving books from DB - updating state
          books: res.data
        });
      })
      .catch(err => {
        console.log("Error from ShowBookList");
      });
  }

  // render method
  render() {

    // actual state
    const books = this.state.books;
    console.log(books);
    return (
      <section className="ShowBookList">
        <div className="container">
          <div className="row">
            <header className="col-md-12">
              <br />
              <h2 className="display-4 text-center">Lista de libros</h2>
            </header>
            <div className="col-md-11">
              <Link
                to="/create-book"
                className="btn btn-outline-warning float-right"
              >
                + AÃ±adir nuevo libro
              </Link>
              <br />
              <br />
              <hr />
            </div>
          </div>
          {/* {console.log("Ka esta pasando"+books)} */}
          <div className="list">
            {/* accessing to bookList variable wich is an array of all books */}
            <div className="card">
              {books.map(book => (
                <BookCard key={book._id} book={book} />
              ))}
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default ShowBookList;
